# Python
def calculator(num1, num2, operator):
    if operator == '+':
        return num1 + num2
    elif operator == '-':
        return abs(num1 - num2)
    elif operator in ['*', '/']:
        return num2 * num1 / num2 if operator == '/' else num1 * num2

